    private void berekenKnopEindActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_berekenKnopEindActionPerformed
        // berekend de totale score van alles en wie de winnaar is
        if (berekenKnop11.isVisible() == false & berekenKnop12.isVisible() == false & berekenKnop21.isVisible() == false & berekenKnop22.isVisible() == false){
            score1 = Integer.parseInt(totaalDeel11.getText()) + Integer.parseInt(totaalDeel12.getText());
            score2 = Integer.parseInt(totaalDeel21.getText()) + Integer.parseInt(totaalDeel22.getText());
            totaalScore1.setText(String.valueOf(score1));
            totaalScore2.setText(String.valueOf(score2));
            if (score1 > score2){
                winnaar.setText("Winnaar: Speler 1!");           
            }else if (score1 == score2){
                winnaar.setText("Gelijk spel!");
            }else if (score1 < score2){
                winnaar.setText("Winnaar: Speler 2!");
            }
        }
        
    }//GEN-LAST:event_berekenKnopEindActionPerformed

